package hello;import com.fasterxml.jackson.annotation.JsonProperty;import io.dropwizard.Configuration;import org.hibernate.validator.constraints.NotEmpty;public class HelloConfiguration extends Configuration {    @NotEmpty    private String template;    @NotEmpty    private String buildNumber;    @JsonProperty    public String getTemplate() {        return template;    }    @JsonProperty    public void setTemplate(String template) {        this.template = template;    }    @JsonProperty    public String getBuildNumber() {        return buildNumber;    }    @JsonProperty    public void setBuildNumber(String buildNumber) {        this.buildNumber = buildNumber;    }}